package tedi;

import org.apache.catalina.Context;
import org.apache.catalina.connector.Connector;
import org.apache.tomcat.util.descriptor.web.SecurityCollection;
import org.apache.tomcat.util.descriptor.web.SecurityConstraint;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.boot.web.embedded.tomcat.TomcatServletWebServerFactory;
import org.springframework.boot.web.servlet.server.ServletWebServerFactory;
import org.springframework.context.annotation.Bean;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.web.servlet.config.annotation.CorsRegistry;
import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;
import tedi.backend.model.Photo;
import tedi.backend.model.Role;
import tedi.backend.model.RoleNames;
import tedi.backend.model.User;
import tedi.backend.recommendation.RecommendationStart;
import tedi.backend.repositories.*;

import java.nio.charset.StandardCharsets;
import java.util.Base64;
import java.util.HashSet;
import java.util.Set;

import static tedi.backend.controllers.Util.compressBytes;

@SpringBootApplication
public class Demo2Application {

    @Bean
    public BCryptPasswordEncoder bCryptPasswordEncoder() {
        return new BCryptPasswordEncoder();
    }
    public static void main(String[] args) {
        SpringApplication.run(Demo2Application.class, args);
    }

    @Bean
    public CommandLineRunner AppStart(RoleRepository roleRepository, UserRepository userRepository, BookingRepository bookingRepository, DepartmentRepository departmentRepository, ReviewRepository reviewRepository,InteractionRepository interactionRepository ,BCryptPasswordEncoder encoder ) {
        return args -> {

            if(roleRepository.findByName(RoleNames.ROLE_HOST) == null )
                roleRepository.save(new Role(RoleNames.ROLE_HOST));
            if(roleRepository.findByName(RoleNames.ROLE_TENANT) == null )
                roleRepository.save(new Role(RoleNames.ROLE_TENANT));



            if(userRepository.findByRole(RoleNames.ROLE_ADMIN).size() == 0){
                User user = new User();
                Set<Role> roles = new HashSet<Role>();
                user.setFirstName("admin");
                user.setLastName("admin");
                user.setUsername("admin");
                user.setPassword(encoder.encode("admin"));
                roles.add(new Role(RoleNames.ROLE_ADMIN));
                user.setRoles(roles);
                user.setEmail("admin@admin.com");
                Photo photo = new Photo("profilePhoto","image/png" ,compressBytes(Base64.getDecoder().decode("".getBytes(StandardCharsets.US_ASCII))));
                user.setProfilePhoto(photo);
                userRepository.save(user);
            }

            //RecommendationStart recStart = new RecommendationStart();
            //recStart.recInit(userRepository , roleRepository ,departmentRepository, reviewRepository , bookingRepository,interactionRepository ,encoder);


        };



    }



    @Bean
    public ServletWebServerFactory servletContainer() {
        TomcatServletWebServerFactory tomcat = new TomcatServletWebServerFactory() {
            @Override
            protected void postProcessContext(Context context) {
                SecurityConstraint securityConstraint = new SecurityConstraint();
                securityConstraint.setUserConstraint("CONFIDENTIAL");
                SecurityCollection collection = new SecurityCollection();
                collection.addPattern("/*");
                securityConstraint.addCollection(collection);
                context.addConstraint(securityConstraint);
            }
        };
        tomcat.addAdditionalTomcatConnectors(redirectConnector());
        return tomcat;
    }

    private Connector redirectConnector() {
        Connector connector = new Connector("org.apache.coyote.http11.Http11NioProtocol");
        connector.setScheme("https");
        connector.setPort(8080);
        connector.setSecure(false);
        connector.setRedirectPort(8443);
        return connector;
    }


    @Bean
    public WebMvcConfigurer corsConfigurer() {
        return new WebMvcConfigurer() {
            @Override
            public void addCorsMappings(CorsRegistry registry) {
                registry.addMapping("/*").allowedOrigins("*");
            }
        };
    }


}
